"use strict";const submitBtn=document.getElementById("subBut");const passRegex=/^.{5,16}$/;const emailRegex=/^[a-z]\w{3,15}@\w{3,10}\.\w{2,6}$/i;const nameRegx=/^[a-z]{2,12}$/i;var displayError=field=>{let icon=document.getElementById(`${field.id}Icon`);icon.classList.add("error","animated","flash");icon.addEventListener("animationend",()=>{icon.classList.remove("animated","flash")});icon.addEventListener("webkitanimationend",()=>{icon.classList.remove("animated","flash","error")})};var removeError=field=>{document.getElementById(`${field.id}Icon`).classList.remove("error")};var valdation=()=>{let password=document.getElementById("password");let email=document.getElementById("email");let name=document.getElementById("name");let noError=!0;if(!emailRegex.test(email.value)){displayError(email);noError=!1}else removeError(email);if(!passRegex.test(password.value)){displayError(password);noError=!1}else removeError(password);if(!nameRegx.test(name.value)){displayError(name);noError=!1}else removeError(name);if(noError)document.getElementById("userForm").submit()};submitBtn.onclick=valdation